import createElem from"./createElem.js";const projectsList=document.querySelector("#projects__items");export default function renderProjects(e){e.forEach(e=>projectsList.append(createProjectItem(e)))}function createProjectItem(e){const t=createElem("li",null,["project__item"]),r=createElem("a",null,null,null,{href:e.link,target:"_blank"});var l=createElem("img",null,["projects__item-image"],null,{src:e.img.src,alt:e.img.alt,width:"360",height:"167","aria-label":e.img.ariaLabel});r.append(l);const a=createElem("span",null,["projects__title"]);return a.innerHTML=`${e.spanTitle}<br><em>${e.spanEm}</em>`,t.append(r,a),t}