import createElem from"./createElem.js";const skillsList=document.querySelector("#skills__list");export default function renderSkills(e){e.forEach(e=>{var{listItemClass:l,header:t,skillsLevel:e}=e;skillsList.append(createItem(l,t,e))})}function createItem(e,l,t){const s=createElem("li",null,["skills__item",e]);l=createElem("p",null,["skills__title"],l);const r=createElem("ul",null,["level"]);s.append(l,r);for(let e=0;e<t.length;e++){const s=createElem("li",null,["level-dot",getSkillsLevel(t[e])]);r.append(s)}return s}function getSkillsLevel(e){switch(e){case 1:return"dot--full";case.5:return"dot--half";default:return"dot--empty"}}