import createElem from"./createElem.js";const projectsList=document.querySelector("#projects__items");export default function renderProjects(e){e.forEach(e=>projectsList.append(createProjectItem(e)))}function createProjectItem(e){const t=createElem("li",null,["project__item"]),r=createElem("span",null,["projects__title"]);r.innerHTML=`${e.spanTitle}<br><em>${e.spanEm}</em>`;const l=createElem("ul",null,["project__item-links"]),n=createElem("li",null,["project__item-link"]);var a=createElem("a",null,["regular-link"],"Link to repository",{href:e.repoLink,target:"_blank"});n.append(a);const c=createElem("li",null,["project__item-link"]);e=createElem("a",null,e.pagesLink?["regular-link"]:["disabled-link"],"Link to GitHub Pages",{href:e.pagesLink||"#",target:"_blank"});return c.append(e),l.append(n,c),t.append(r,l),t}